import { domLeftTop } from '@/components/util/Dom.js';
import { getNowDateTime } from '@/common/util/Dates.js';
import { addDateTime } from '@/common/util/Dates.js';
import { getDays } from '@/common/util/Dates.js';
export default {
  name: "InputDateTimeRange",
  props: {
    label: String,
    // 标题文本
    value: Array,
    // 当前值(v-model)
    type: {
      type: String,
      default: 'YMDHN'
    },
    placeholder: String,
    required: Boolean,
    // 是否显示必填
    disabled: Boolean,
    // 是否可操作
    clearable: Boolean,
