      type: Number
    },
    // lable宽度最小数值，单位是px
    labelMinWidth: {
      type: Number
    }
  },
  _format: null,
  data: function data() {
    return {
      values: [{
        year: null,
        month: null,
        day: null,
        hour: null,
        minute: null,
        second: null
      }, {
        year: null,
        month: null,
        day: null,
        hour: null,
        minute: null,
        second: null
      }],
      needs: {
        year: false,
        month: false,
        day: false,
        hour: false,
        minute: false,
        second: false
