export default {
  name: "InputLabel",
  props: {
    label: String,
    // 标题文本
    required: Boolean,
    // 是否必填
    border: {
      type: Boolean,
      default: false
    },
    // 是否显示边框
    borderColor: {
      type: String,
      default: $_theme.border_color_light
    },
    minHeight: {
      type: Number,
      default: $_theme.inputHeight
    },
    // 高度数值，单位是px
    labelMinWidth: {
      type: Number,
      default: 10
    } // lable宽度最小数值，单位是px

  },
  computed: {
    containerStyle() {
      return {
        minHeight: this.minHeight + 'px',
        borderColor: this.borderColor
      };
    },

    labelStyle() {
      return {
        minWidth: this.labelMinWidth + 'px',
        borderRightColor: this.theme.border_color_light
      };
    }

  }
};
