/*
* 使用例子
* <InputRadio label="你要选择哪一个" :options="[{label:'吃饭',value:1},{label:'睡觉',value:2}]" v-model="want" @change="onRadioChg"/>
*
*  data: {want: 1},
*  methods: {
*    onRadioChg(value) {// value值是1或2
*    }
*  }
*/
export default {
  name: "InputRadio",
  props: {
    label: String,
    // 标题文本
    value: [String, Number, Boolean],
    // 当前值(v-model)
    options: Array,
    // 多个选项 [{lable:xxx,value:xxx}...]
    labelField: {
      type: String,
      default: 'label'
    },
    // 显示字段
    valueField: {
      type: String,
      default: 'value'
    },
    // 值字段
    unit: String,
    // 单位
    required: Boolean,
    // 是否必填
    disabled: Boolean,
    // 是否可操作
    border: {
      type: Boolean,
      default: true
    },
    // 是否显示边框
    vertical: {
      type: Boolean,
      default: false
    },
    // 是否垂直布局
    height: {
      type: Number,
      default: $_theme.inputHeight
    },
    // 高度数值，单位是px
    labelMinWidth: {
      type: Number,
      default: 10
    },
    // lable宽度最小数值，单位是px
    groupStyle: {
      default: null
    }
  },

  data() {
    return {
      groupName: window.$_uuid('RadioGroup'),
      focused: false
    };
  },

  methods: {
    isChecked(item) {
      item = item[this.valueField];
      return item === this.value || "" + item === "" + this.value;
    },

    handleChange(event, item) {
      if (this.disabled) {
        return false;
      }

      let value = event.target.value;

      if (this.valueType == 'number') {
        value = Number(value);
      } else if (this.valueType == 'boolean') {
        value = value == 'true';
      }

      if (this.value !== value) {
        this.$emit('input', value);
        this.$emit('change', value, item);
      }
    }

  },
  computed: {
    valueType() {
      if (this.options && this.options.length > 0) {
        return typeof this.options[0][this.valueField];
      }

      return null;
    },

    containerStyle() {
      return {
        height: this.height + 'px',
        borderColor: this.focused ? this.theme.color : this.theme.border_color_light,
        zIndex: this.focused ? this.theme.zindex() : ''
      };
    },

    labelStyle() {
      return {
        minWidth: this.labelMinWidth + 'px',
        borderRightColor: this.theme.border_color_light,
        backgroundColor: this.theme.bg_color
      };
    },

    radioLabelStyle() {
      return {
        display: this.vertical ? 'block' : 'inline-block'
      };
    }

  }
};
