/*
* 使用例子
* <InputTSelect label="你要选择哪一个" :options="[{label:'吃饭',value:1},{label:'睡觉',value:2}]" v-model="like" @change="onSelectChg"/>
*
*  data: {like: 1},
*  methods: {
*    onSelectChg(value, item) {// value值是1或2, item是{label:'吃饭',value:1}
*    }
*  }
*/
import { domLeftTop } from '@/components/util/Dom.js';
import { arrayFindItem } from '@/common/util/Arrays.js';
import { arrayRemoveValue } from '@/common/util/Arrays.js';
import { arrayGetValue } from '@/common/util/Arrays.js';
import storage from '@/common/cache/Storage.js';

export default {
  name: "InputTSelect",
  props: {
    label: String,                                                      // 标题文本
    value: { default: null },                                           // 当前值(v-model)
    options: Array,                                                     // 多个选项 [{lable:xxx,value:xxx}...]
    labelField: { type: String, default: 'label' },                     // 显示字段
    valueField: { type: String, default: 'value' },                     // 值字段
    checkbox: { type: Boolean, default: false },                        // 是否多选
    showCheckAll: { type: Boolean, default: true },                     // 是否显示全选
    splitter: { type: String, default: null },                          // 如果是多选，而value是字符串"1,2,3,4" 则需要定义拆分符为逗号
    placeholder: String,
    unit: String,                                                       // 单位
    required: Boolean,                                                  // 是否显示必填
    disabled: Boolean,                                                  // 是否可操作
    clearable: Boolean,                                                 // 是否显示可清除图标
    focusable: { type: Boolean, default: true },                        // 是否可focus
    showArrow: { type: Boolean, default: true },                        // 是否显示箭头图标
    border: { type: Boolean, default: true },                           // 是否显示边框
    borderColor: { type: String, default: $_theme.border_color_light },
    radius: { type: String,  default: $_theme.input_radius},            // 边框圆角
    focusColor: { type: String, default: $_theme.color },
    height: { type: Number, default: $_theme.inputHeight },             // 高度数值，单位是px
    maxListNum: { type: Number, default: 10 },                          // 下拉框最多显示10个选项，多出10个选项会出现滚动条
    afterOpen: Function,
    beforeClose: Function,
    labelMinWidth: { type: Number, default: 10 },  		  	              // lable宽度最小数值，单位是px
    inputWidth: { type: String, default: null },                        // 输入框的宽度
    dropdownWidth: { type: String, default: null },                     // 选择列表的宽度
    contentStyle: { default: null },
    listStyle: { default: null },
    labelColor: { type: String, default: null },                        // lable颜色
    selectedColor: { type: String, default: null },                     // 选项选中的颜色
    searchable: { type: Boolean, default: false },                      // 是否显示搜索栏
    isColorBlock: { type: Boolean, default: false },                    // 是否显示色块
    searchKey: { typeof: String, default: null },
    autocomplete:   { type: String,  default: 'off' },                // 自动补全
  },
  data() {
    return {
      selectedItem: null,
      hoverItem: null,
      focused: false,
      selectorHover: false,
      createDropDown: false,
      showDropDown: false,
      dropdownStyle: null,
      checkedItems: null,
      checkMap: {},
      checkAll: false,
      searchValue: null,
      searchOption: [],
      showUl: false,
      listMaxHeight: 0,
    };
  },
  watch: {
    value: function (v) {
      if (this._myself) return this._myself = false;
      this.checkbox ? this.setCheckedItems(v) : this.setSelectedItem(v);
    },
    focused: function (v) {
      this.$emit('focused', v);
    }
  },
  mounted() {
    if (this.value === null || this.value == undefined) return;
    this.checkbox ? this.setCheckedItems(this.value) : this.setSelectedItem(this.value);
  },
  methods: {
    setSelectedItem(v) {
      this.inupttext=v
      if (v === null || v == undefined) return this.selectedItem = null;
      if (!this.selectedItem || this.selectedItem[this.valueField] != v) {
        this.selectedItem = arrayFindItem(this.options, this.valueField, v)||v;
      }
    },
    toggleDropDown(event) {// 显示或隐藏选项列表
      if (!this.options || this.options.length == 0) return;
      this.focused = true;
      if (this.showDropDown) return this.hideDropDown(event);
      this.showDropDown = this.createDropDown = true;

      // 自动判断是显示在下面还是上面
      let { left, top } = domLeftTop(this.$refs.selector), isbottom = true, listHeight = 30 * Math.min(this.maxListNum, this.options.length) + 1 + (this.border ? 0 : 1);
      let this_height = this.$el.offsetHeight;
      let down = document.documentElement.clientHeight - top - this_height;
      if (down > listHeight) {// 下面空间充足，显示在下面
        top += this_height - (this.border ? 1 : 0);
      } else if (top > listHeight) {// 上面空间足够，显示在上面
        top -= listHeight + (this.border ? 1 : 0);
        isbottom = false;
      } else {// 上面和下面的空间都不够
        if (down > top) {// 下面的空间大，显示在下面
          listHeight = down - 5;
          top += this_height - (this.border ? 1 : 0);
        } else {// 上面的空间大，显示在上面
          listHeight = top - 5;
          top -= listHeight + (this.border ? 1 : 0);
          isbottom = false;
        }
      }
      this.listMaxHeight = listHeight;
      this.border && (left--);
      this.dropdownStyle = {
        borderColor: this.borderColor,
        zIndex: window.$_theme.zindex(),
        top: top + 'px',
        left: left + 'px',
        // marginLeft: (this.$refs.selector.offsetLeft) + 'px',
        // marginTop: isbottom ? this.border ? '1px' : '0' : '0',
        width: this.dropdownWidth || ((this.$el.offsetWidth - this.$refs.selector.offsetLeft - (this.border?1:0)) + 'px')
      };

      // !isbottom && this.$nextTick(() => this.dropdownStyle.marginTop = -(this.$refs.dropdown.offsetHeight+this.$el.offsetHeight+(this.border?-1:0)) + 'px');
      this.border && (this.dropdownStyle[isbottom ? 'border-top-width' : 'border-bottom-width'] = 0);
      this.afterOpen && this.$nextTick(this.afterOpen);
      document.addEventListener('click', this.onMouseClick);
    },
    onMouseClick(event) {
      !this.$el.contains(event.target) && this.hideDropDown(event);
    },
    handleFocus(event) {
      this.focused = true;
      this.$emit('focus', event);
    },
    handleBlur(event) {
      this.focused = false;
      this.$emit('blur', event);
    },
    hideDropDown(event) {// 隐藏弹出的选项列表
      if (this.beforeClose && this.beforeClose(event) === false) return false;
      this.showDropDown = false;
      document.removeEventListener('click', this.onMouseClick);
      return true;
    },
    handleInput(event) {
      let searchOption = []
      this.searchValue = event.target.value;
      this.searchValue = this.searchValue.trim();
      this.$refs.clearBtn.style.display = (this.searchValue) ? '' : 'none';
      this.options.forEach(el => {
        if (el[this.labelField].indexOf(this.searchValue) != -1) {
          searchOption.push(el);
        }
      })
      this.searchOption = searchOption;
    },
    handleInput2(event){
      let value=event.target.value;
      this.inupttext=value
      this.$emit('input', value);
    },
    onSelectItem(item, event) {
      if (this.checkbox) return;
      this.selectorHover = false;
      if (!this.selectedItem || this.selectedItem[this.valueField] !== item[this.valueField]) {
        let old = this.value, value = item[this.valueField];
        this.selectedItem = item;
        this._myself = true;
        this.$emit('input', value);
        this.$emit('change', value, item, old);
        this.searchKey && storage.set(this.searchKey, value);
      }
      this.hideDropDown(event);
      // if (!this.hideDropDown(event) && !this.dropdownWidth) {
      //   this.$nextTick(()=>this.dropdownStyle.width = ((this.$el.offsetWidth - this.$refs.selector.offsetLeft - (this.border?1:0)) + 'px'));
      // }
    },
    onCheck(item, checked) {
      this.onBeforeChange(this.checkMap, this.checkedItems);
      this.checkMap[item[this.valueField]] = checked;
      this.checkedItems = this.checkedItems || [];
      checked ? this.checkedItems.push(item) : arrayRemoveValue(this.checkedItems, this.valueField, item[this.valueField]);
      let values = arrayGetValue(this.checkedItems, this.valueField);
      if (this.splitter) values = values.join(this.splitter);
      this._myself = true;
      let oldValue = this.value;
      this.$emit('input', values);
      this.$emit('change', { value: values, text: this.text, item: item, checked: checked, oldValue: oldValue });
      this.searchKey && storage.set(this.searchKey, values);
      this.checkAll=this.options.length==values.length&&true||false;
    },
    onBeforeChange(value1, value2) {
      this.$emit('onBeforechange', value1, value2)
    },
    onCheckAll(checked) {
      if (!checked) {
        this.checkMap = {};
        this.checkedItems = null;
        let old = this.value;
        this._myself = true;
        this.$emit('input', null);
        this.$emit('change', { value: null, text: null, oldValue: old });
        this.searchKey && storage.remove(this.searchKey);
      } else {
        this.checkedItems = [...this.options];
        let values = arrayGetValue(this.checkedItems, this.valueField);
        let map = {};
        values.forEach(v => map[v] = true);
        this.checkMap = map;
        if (this.splitter) values = values.join(this.splitter);
        this._myself = true;
        let oldValue = this.value;
        this.$emit('input', values);
        this.$emit('change', { value: values, text: this.text, checked: checked, oldValue: oldValue });
        this.searchKey && storage.set(this.searchKey, values);
      }
    },
    setCheckedItems(v) {
      if (v === null || v == undefined) {
        this.checkMap = {};
        this.checkedItems = null;
        return;
      }
      let values = Array.isArray(v) ? v : (this.splitter ? v.split(this.splitter) : []);
      let map = {}, list = [], item;
      values.forEach(value => {
        map[value] = true;
        item = arrayFindItem(this.options, this.valueField, value);
        item && list.push(item);
      });
      this.checkMap = map;
      this.checkedItems = list;
      this.checkAll=this.options.length==values.length&&true||false;
    },
    getSelectedItem() {
      return this.selectedItem;
    },
    getCheckedItems() {
      return this.checkedItems;
    },
    clear() {
      this.showDropDown && this.hideDropDown();
      if (this.checkbox) {
        this.checkedItems = null;
        this.checkMap = {};
      } else {
        this.selectedItem = null;
      }
      let old = this.value;
      this._myself = true;
      this.inupttext=null;
      this.$emit('input', null);
      this.searchKey && storage.remove(this.searchKey);
      this.checkbox ? this.$emit('change', { value: null, text: null, checked: false, oldValue: old }) : this.$emit('change', null, old);
    },
    optionItemStyle(item, index) {
      if (!this.showDropDown) return null;
      if (item == this.hoverItem) {
        return { backgroundColor: this.theme.list_hover_color };
      } else if (this.selectedItem && this.selectedItem[this.valueField] === item[this.valueField]) {
        return { backgroundColor: this.selectedColor ? this.selectedColor : this.theme.selected_color };
      } else {
        return { backgroundColor: this.theme.alternate_colors[index % 2] };
      }
    },
    handleClick() {
      this.showUl = true;
    },
    clearSearch() {
      this.searchValue = null;
      this.searchOption = [];
      this.$refs.clearBtn.style.display = 'none';
    },
    ClickItem(item, event) {
      this.showUl = false;
      this.onSelectItem(item, event)
      if (this.checkbox) {
        this.onCheck(item, true)
      }

    },
  },
  computed: {
    text: function () {
      let text;
      if (!this.checkbox){ text= this.selectedItem ? this.selectedItem[this.labelField] : null;}
      else if (!this.checkedItems || this.checkedItems.length == 0){text=null}
      else{text=arrayGetValue(this.checkedItems, this.labelField).join(',');}
      return text||this.inupttext
    },
    showClear() {// 判断是否要显示清除按钮
      return this.clearable && this.selectorHover && this.text;
    },
    containerStyle() {
      return {
        borderRadius: this.radius,
        height: this.height + 'px',
        borderColor: (this.focused && this.focusable) ? this.focusColor : this.borderColor,
        zIndex: this.focused ? this.theme.zindex() : ''
      };
    },
    lineHeight() {// 获取除边框外的高度
      return this.height - (this.border ? 2 : 0);
    },
    labelStyle() {
      return {
        minWidth: this.labelMinWidth + 'px',
        borderRightColor: this.theme.border_color_light,
        backgroundColor: this.labelColor ? this.labelColor : this.theme.input_label_bg_color
      };
    }
  }
};
