.container {
  outline: none;
  position: relative;
  display: inline-block;
  white-space: nowrap;
}
.container > * {
  height: 100%;
  display: flex;
  align-items: center;
}
.border {
  border-style: solid;
  border-width: 1px;
}
/* 标题文本 */
.label {
  float: left;
  padding: 0 6px;
  border-right-style: solid;
  border-right-width: 0px;
  white-space: nowrap;
  user-select: none;
}
/* 输入框 */
.input {
  height: 100%;
  display: inline-block;
  border: 0;
  outline: 0;
  padding: 0 8px;
  background-color: #fff;
}
.input::placeholder {
  color: #B0B0B0;
}
.input::-webkit-input-placeholder {
  color: #B0B0B0;
}
.input:-ms-input-placeholder {
  color: #B0B0B0;
}
.input::-ms-input-placeholder {
  color: #B0B0B0;
}
/* 清空按钮 */
.clear {
  float: right;
  position: relative;
  width: 0px;
}
.clear > i {
  position: absolute;
  cursor: pointer;
  left: -20px;
  width: 20px;
  font-size: 16px;
  line-height: 0;
}
.clear > i:first-child {
  top: 0;
  background-color: #fff;
  height: calc(100% - 2px);
}
/* 单位 */
.unit {
  float: right;
  padding-right: 8px;
  white-space: nowrap;
  background-color: #fff;
  user-select: none;
}
/* 必填 */
.required {
  display: flex!important;
  float: right;
  padding: 0 2px;
  color: #f00;
}
/* 禁止操作 */
.disabled {
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  background-color: rgba(255, 255, 255, 0.6);
  opacity: 0.6;
  filter: alpha(opacity=60); 
  cursor: not-allowed;
}
