import SearchBox from "@/components/Tree/SearchBox.vue";

import { domLeftTop } from '@/components/util/Dom.js';
import { objCopyTo } from '@/common/util/Objects.js';
import { objIsEmpty }  from '@/common/util/Objects.js';
import { Fields } from '@/common/cache/CacheTeamCar.js';
import { getCarTreeCfg } from '@/common/cache/CacheTeamCar.js';
import { saveCarTreeCfg } from '@/common/cache/CacheTeamCar.js';
import { countState } from '@/common/cache/CacheTeamCar.js';

export default {
  components: { SearchBox },
  props: {
    setting:    { default: null},
    miniBar:    { type: Boolean, default: false },
    treeBorder: { type: Boolean, default: true  },
    onSearchItemSelected: Function,
    refreshClick: Function,
    onCarFilterChg: Function,
    showRecent: Function,
  },

  _carFields: null,
  _teamFields: null,
  _tree: null,
  _treeId: null,

  data() {
    return {
      uuid: $_theme.zindex(),
      placeholder: "",
      hasOwnField: null,
      model: false,
      carTreeCfg: {},
      showTeamCount: {line: false, move: false, alarm: false, invalid: false},
      showCarField: [], //['color', 'tmnKey', 'state'], 
      searchSet: {show: false, left:0, top:0},
      renderSet: {show: false, left:0, top:0},
      simple: false,
    };
  },

  computed: {
    hw: function() { return this.miniBar ? 27 : this.theme.inputHeight; },
    mini: function() { return this.miniBar },
  },

  watch: {
    'searchSet.show': function(v) {
      if (!v) return document.removeEventListener('click', this.clickSearchSet);
      let {left, top} = domLeftTop(this.$el);
      this.searchSet.left = left - 1;
      this.searchSet.top = top + this.$el.clientHeight;
      setTimeout(() => { document.addEventListener('click', this.clickSearchSet); }, 10);
    },
    'renderSet.show': function(v) {
      if (!v) return document.removeEventListener('click', this.clickRenderSet);
      let {left, top} = domLeftTop(this.$el);
      if (left + this.$el.clientWidth + 260 > document.documentElement.clientWidth) {
        left = left + this.$el.clientWidth - 260 + 1;
        top += this.$el.clientHeight;
      } else {
        left = left + this.$el.clientWidth;
        top -= 1;
      }
      this.renderSet.left = left;
      this.renderSet.top = top;
      setTimeout(() => { document.addEventListener('click', this.clickRenderSet); }, 10);
    }
  },

  mounted() {
    this.carTreeCfg = getCarTreeCfg();
    this.carTreeCfg.showTeamCount && objCopyTo(this.carTreeCfg.showTeamCount, this.showTeamCount);
    if (this.carTreeCfg.showCarField) {
      this.carTreeCfg.showCarField.tmnKey  && this.showCarField.push('tmnKey');
      this.carTreeCfg.showCarField.state   && this.showCarField.push('state');
    }
  },

  methods: {
    init() {
      this.setSearchField();
    },
    setTree(tree) {
      this._treeId = tree.setting.treeId;
      this.$refs.searchBox.setTree(this._tree = tree);
    },
    setSearchField() {
      let carFields = this.carTreeCfg.search.carFields, teamFields = this.carTreeCfg.search.teamFields;
      if (carFields.length == 0 && teamFields.length == 0) {
        carFields.push('plate');
      }
      this._carFields = carFields.length > 0 ? carFields : null;
      this._teamFields = teamFields.length > 0 ? teamFields : null;
      this.hasOwnField = this._carFields && this._teamFields ? null : this._carFields ? 'plate' : 'pid';
      let placeholder = [];
      if (this._carFields) {
        this._carFields.indexOf('plate')  > -1 && placeholder.push('车牌号码');
        this._carFields.indexOf('carname') > -1 && placeholder.push('车辆名称');
        this._carFields.indexOf('tmnKey') > -1 && placeholder.push('终端标识');
      }
      if (this._teamFields) {
        this._teamFields.indexOf('teamname') > -1 && placeholder.push('车队名称');
        this._teamFields.indexOf('linkman') > -1 && placeholder.push('联系人员');
        this._teamFields.indexOf('phone')   > -1 && placeholder.push('联系电话');
      }
      this.placeholder = placeholder.join('｜');
    },
    searchFun(node, text) {
      if (node[this._treeId].hide) return false;
      return this.contain(node, text, node.plate ? this._carFields : this._teamFields);
    },
    contain(node, text, fields) {
      if (!fields) return false;
      for (let field of fields) {
        field = (Fields[field] || Fields.bxts)(node, field);
        if (field && field.toLowerCase().indexOf(text) >= 0) return true;
      }
      return false;
    },
    clickSearchSet(event) {
      this.searchSet.show = this.$refs.searchSet.contains(event.target);
    },
    clickRenderSet(event) {
      this.renderSet.show = this.$refs.renderSet.contains(event.target);
    },
    searchSetChg() {
      saveCarTreeCfg();
      this.setSearchField();
    },
    setTeamCount(field) {
      let before = this.carTreeCfg.showTeamCount;
      this._setShowCfg('showTeamCount', field, this.showTeamCount[field]);
      !before && this.carTreeCfg.showTeamCount && countState();
      this.resetTree(this._tree.updateNodeName, false);
    },
    carSortFieldChg() {
      saveCarTreeCfg();
      this.resetTree(this._tree.updateNodeName, true);
    },
    showCarFieldChg(field) {
      this._setShowCfg('showCarField', field, this.showCarField.indexOf(field) != -1);
      this.resetTree(this._tree.updateNodeName, true);
    },
    carFilterChg() {
      saveCarTreeCfg();
      this.onCarFilterChg(this.carTreeCfg.filter);
      this._tree && this.carTreeCfg.filter && (this._tree.setting.view.hideFunction = this.$parent.hideFunction);
      this.resetTree(this._tree.updateNodeHide, true);
      this._tree && !this.carTreeCfg.filter && (this._tree.setting.view.hideFunction = null);
    },
    _setShowCfg(item, key, value) {
      let cfg = this.carTreeCfg[item];
      if (!cfg && !value) return;
      if (!value) {
        cfg && delete cfg[key];
        if (objIsEmpty(cfg)) cfg = false;
      } else {
        cfg = cfg || {};
        cfg[key] = value;
      }
      this.carTreeCfg[item] = cfg;
      saveCarTreeCfg();
    },
    resetTree(method, resetCar, nodes=null) {
      nodes = nodes || this._tree.getNodes();
      for (let i=0,len=nodes.length,node; i<len; i++) {
        node = nodes[i];
        if (!node[this._treeId].dom) continue;
        if (node.plate) {
          resetCar && method(node);
          continue;
        } else if (!resetCar) {
          method(node);
        }
        if (node.children && node.children.length > 0) {
          this.resetTree(method, resetCar, node.children);
        }
      }
    },
    showTip(tip, color) {
      this.$refs.searchBox.clear();
      this.$refs.searchBox.showTip(tip, color);
    },
    destroy() {
      this.$refs.searchBox.clear();
      this.$refs.searchBox.setTree(null);
      this._tree = null;
      this._carFields = null;
      this._teamFields = null;
    }
  }
}
