.container {
  outline: none;
  position: relative;
  display: inline-block;
  white-space: nowrap;
  background-color: #fff;
  user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  -moz-user-select: none;
}
.container > * {
  height: 100%;
  display: flex;
  align-items: center;
}
.border {
  border-style: solid;
  border-width: 1px;
}
/* 标题文本 */
.label {
  float: left;
  padding: 0 6px;
  border-right-style: solid;
  border-right-width: 0px;
  white-space: nowrap;
}
/* radio组 */
.radioGroup {
  overflow: hidden;
  padding-left: 8px;
}
/* radio项(按钮+文字) */
.radioItem {
  margin: 6px 8px 6px 0px;
  white-space: nowrap;
  background-repeat: no-repeat;
  background-position-y: center;
  cursor: pointer;
}
/* 单位 */
.unit {
  float: right;
  padding-right: 8px;
  white-space: nowrap;
}
/* 必填 */
.required {
  display: flex !important;
  float: right;
  padding: 0 2px;
  color: #f00;
}
/* 禁止操作 */
.disabled {
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  background-color: rgba(255, 255, 255, 0.6);
  opacity: 0.6;
  filter: alpha(opacity=60);
  cursor: not-allowed;
}
