import SearchBox from "@/components/Tree/SearchBox.vue";
import { getTeams, onTeamTreeAdd, onTeamTreeRemove } from '@/common/cache/CacheTeamCar.js';
import { refreshAll } from '@/common/cache/CacheTeamCar.js';
import { regetNodes } from '@/common/cache/CacheTeamCar.js';
import { objClone } from '@/common/util/Objects.js';

export default {
  components: { SearchBox },
  props: {
    setting:        { default: ()=>{return {}} },                               // 参考ztree的setting配置
    frozenRoot:     { type: Boolean, default: false },                          // 冻结根结点，不显示根结点的+-图标
    disabled:       { type: Boolean, default: false },                          // 是否可操作
    border:         { type: Boolean, default: true  },                          // 是否显示边框
    borderColor:    { type: String,  default: $_theme.border_color_dark},       // 边框颜色
    searchable:     { type: Boolean, default: true  },                          // 是否显示搜索栏
    placeholder:    String,
    miniBar:        { type: Boolean, default: false },                          // 是否显示搜索栏
    showSearchIcon: { type: Boolean, default: true  },                          // 是否显示搜索图标
    onSearchItemSelected: Function,                                             // 选中了搜索栏查出的某个结点的回调函数，参数(node)
    onTreeInited:         Function,
    onTreeRefreshed: Function,
  },

  _tree: null,

  getTreeRoots: callback => getTeams(cache => callback(cache.root)),
  getNodeText: node => node.name,

  data() {
    return {
      treeId: 'tree_' + $_theme.zindex(),
      loading: false,
    };
  },

  watch: {
    'setting.view.nameFunction': function(v) {
      if (!this._tree) return;
      this._tree.setting.view.nameFunction = v;
      this._updateNodeName(this._tree.getNodes());
    }
  },

  mounted() {
    this.loading = true;
    getTeams(this.onLoaded);
  },

  methods: {
    onLoaded(cache) {
      if (!this._setting) {
        this._setting = objClone(this.setting);
        this._setting.data = this._setting.data || {};
        this._setting.data.key = this._setting.data.key || {};
        this._setting.data.key.children = 'teams';
        this._setting.view = this._setting.view || {};
        this._setting.view.iconCssFunction = this.iconCssFunction;
      }

      cache.root[this.treeId] = {open: true};
      this._tree = $.fn.zTree.init($(this.$refs.treeUl), this._setting, cache.root);
      this.searchable && this.$refs.searchBox.setTree(this._tree);
      this.loading = false;
      onTeamTreeAdd(this.treeId, this);
      this.onTreeInited && this.onTreeInited(cache.root,this._tree,this);
    },
    refreshClick() {
      this.loading = true;
      refreshAll(e => {
        this.loading = false;
        this.searchable && this.showTip('刷新失败', '#f00');
      });
    },
    onRefresh(cars,teams) {
      let snodes = this._tree.getSelectedNodes();
      let cnodes = (this._setting.check && this._setting.check.enable) ? this._tree.getCheckedNodes(true,true) : null;
      this.onLoaded(teams);
      this.searchable && this.showTip('刷新成功', '#00f');
      snodes = regetNodes(snodes);
      snodes.length > 0 && this._tree.selectNode(snodes[0]);
      cnodes = regetNodes(cnodes);
      cnodes && cnodes.length > 0 && this.checkNodes(cnodes, true, true);
      this.onTreeRefreshed && this.onTreeRefreshed(snodes,cnodes);
    },
    showTip(tip, color) {
      this.$refs.searchBox.clear();
      this.$refs.searchBox.showTip(tip, color);
    },
    iconCssFunction(node, open) {
      return open ? 'ico_open' : 'ico_close';
    },
    _updateNodeName(nodes) {
      for (let i=0,len=nodes.length,node; i<len; i++) {
        if (this._tree.updateNodeName(node = nodes[i])) {
          node.teams && this._updateNodeName(node.teams);
        }
      }
    },
    getCheckedNodes(onlyChecked=true, onlyTop=false, field=null, hasOwnField=null, nodes=null) {
      return this._tree.getCheckedNodes(onlyChecked, onlyTop, field, hasOwnField, nodes);
    },
    checkNodes(nodes, checked=true, checkTypeFlag, callbackFlag) {
      for (let i=0,len=nodes.length; i<len; i++) {
        this._tree.checkNode(nodes[i], checked, checkTypeFlag, callbackFlag);
      }
    },
    selectNode(node, isSilent=false) {
      this._tree.selectNode(node, false, isSilent);
    },
    getTree() { return this._tree; },
    getTreeId() { return this.treeId; },
    destroy() {
      onTeamTreeRemove(this.treeId);
      if (this._tree) {
        this._tree.destroy();
        this._tree = null;
      }
      if (this.searchable) {
        this.$refs.searchBox.clear();
        this.$refs.searchBox.setTree(null);
      }
    }
  },

  beforeDestroy() {
    this.destroy();
  }
}
