/*
* 使用例子
* <ButtonBar :options="[{label:'吃饭',value:1},{label:'睡觉',value:2}]" v-model="like" :beforeChange="onButtonBeforeChg" @change="onButtonChg"/>
* 
*  data: {like: 1},
*  methods: {
*    onButtonBeforeChg(oldValue, newValue) {
*      return true;  // 允许变化
*      return false; // 不允许变化
*    },
*    onButtonChg(value)) {// value值是1或2
*    }
*  }
* 
* options单值模式：
* <ButtonBar :options="[1,2,3,4,5]" .../>
* <ButtonBar :options="['aa','bb']" .../>
*/

export default {
  name: "ButtonBar",
  props: {
    value: [String, Number],                                           // 当前值(v-model)
    options: Array,                                                    // 多个选项 [{lable:xxx,value:xxx}...]
    labelField:  { type: String,  default: 'label' },                  // 显示字段
    valueField:  { type: String,  default: 'value' },                  // 值字段
    iconField:   { type: String,  default: null },                     // 图标字段
    mode:        { type: String,  default: 'button' },                 // 模式：button按钮模式　underline下划线模式 excel底部切换模式
    gap:         { type: Number,  default: 0 },                        // 按钮间的间距
    widthIsSet:  { type: Boolean, default: true },                     // 是否设置了宽度，如果没设置，按钮宽度自适应内容
    closable:    { type: Boolean, default: false },                    // 按钮是否可关闭
    vertical:    { type: Boolean, default: false },                    // 是否垂直显示
    position:    { type: String,  default: 'top' },                    // top bottom left right center
    padding:     { type: String,  default: '0 10px' },                 // 按钮padding
    border:      { type: Boolean, default: true },                     // 是否有边框
    borderColor: { type: String,  default: $_theme.border_color_dark}, // 边框颜色
    radius:      { type: String,  default: $_theme.tab_radius},        // 边框圆角
    disabled: Boolean,                                                 // 是否可操作
    height:      { type: Number,  default: $_theme.inputHeight },      // 高度数值，单位是px,
    splitLine:   {type: Boolean, default: false},                      // 是显示分隔线
    beforeChange: Function,
    buttonStyle: { default: null },
    containerBgc: { type: String, default:'#c1c9da'}                      //显示ButtonBr背景颜色
  },
  _single: null,  //是否是单值，也就是options = [1,2,3]/['a','bb','ccc']
  data() {
    return {  
      selectedIndex: null, // 选中项的index
    };
  },
  created() {
    this._single = false;
    if (this.options != null && this.options.length) {
      let type = typeof(this.options[0]);
      if (type == 'string' || type == 'number') {
        this._single = true;
      }
    }
  },
  methods: {
    getItemValue(item) {
      return this._single ? item : item[this.valueField];
    },
    getItemText(item) {
      return this._single ? item : item[this.labelField];
    },
    handleClick(value, index) {
      if (this.value !== value){
        this.selectedIndex = index;
        if (!this.beforeChange || this.beforeChange(value, this.value) !== false) {
          let old = this.value;
          this.$emit('input', value);
          this.$emit('change', value, old);
        }
      }
      this.$emit('click', value);
    },
    mouseenter(event) {
      this.closable && (event.target.children[0].style.display ='block');
    },
    mouseleave(event) {
      this.closable && (event.target.children[0].style.display ='none');
    },
    onCloseClick(item) {
      this.$emit('close', item);
    },
    boxShadowStyle(index) {
      return this.mode == 'underline' ? null : {boxShadow: `${(this.vertical||index==0)?'0':'-1px'} ${!this.vertical||index==0?'0':'-1px'} 0 0 ${this.mode == 'excel'?this.borderColor:this.theme.color}`}
    },
  },
  computed: {
    containerStyle() {
      let style = this.vertical ? {flexFlow: 'column'} : {
        height: this.height + 'px',
        lineHeight: this.height + 'px'
      };
      switch (this.position) {
        case 'top': return Object.assign(style, {borderTopLeftRadius:this.radius,borderTopRightRadius:this.radius});
        case 'bottom': return Object.assign(style, {borderBottomLeftRadius:this.radius,borderBottomRightRadius:this.radius});
        case 'left': return Object.assign(style, {borderTopLeftRadius:this.radius,borderBottomLeftRadius:this.radius});
        case 'right': return Object.assign(style, {borderTopRightRadius:this.radius,borderBottomRightRadius:this.radius});
      }
      return Object.assign(style, {borderRadius:this.radius});
    },
    firstStyle() {
      if (this.mode == 'excel') return null;
      switch (this.position) {
        case 'top': return this.gap>1 ? {borderTopLeftRadius:this.radius,borderTopRightRadius:this.radius} : {borderTopLeftRadius:this.radius};
        case 'bottom': return this.gap>1 ? {borderBottomLeftRadius:this.radius,borderBottomRightRadius:this.radius} : {borderBottomLeftRadius:this.radius};
        case 'left': return this.gap>1 ? {borderTopLeftRadius:this.radius,borderBottomLeftRadius:this.radius} : {borderTopLeftRadius:this.radius};
        case 'right': return this.gap>1 ? {borderTopRightRadius:this.radius,borderBottomRightRadius:this.radius} : {borderTopRightRadius:this.radius};
      }
      return this.vertical ? {borderTopLeftRadius:this.radius,borderTopRightRadius:this.radius} : {borderTopLeftRadius:this.radius,borderBottomLeftRadius:this.radius};
    },
    middleStyle() {
      if (this.mode == 'excel') return null;
      switch (this.position) {
        case 'top': return this.gap>1 ? {borderTopLeftRadius:this.radius,borderTopRightRadius:this.radius} : null;
        case 'bottom': return this.gap>1 ? {borderBottomLeftRadius:this.radius,borderBottomRightRadius:this.radius} : null;
        case 'left': return this.gap>1 ? {borderTopLeftRadius:this.radius,borderBottomLeftRadius:this.radius} : null;
        case 'right': return this.gap>1 ? {borderTopRightRadius:this.radius,borderBottomRightRadius:this.radius} : null;
      }
      return null;
    },
    lastStyle() {
      if (this.mode == 'excel') return null;
      switch (this.position) {
        case 'top': return this.gap>1 ? {borderTopLeftRadius:this.radius,borderTopRightRadius:this.radius} : {borderTopRightRadius:this.radius};
        case 'bottom': return this.gap>1 ? {borderBottomLeftRadius:this.radius,borderBottomRightRadius:this.radius} : {borderBottomRightRadius:this.radius};
        case 'left': return this.gap>1 ? {borderTopLeftRadius:this.radius,borderBottomLeftRadius:this.radius} : {borderBottomLeftRadius:this.radius};
        case 'right': return this.gap>1 ? {borderTopRightRadius:this.radius,borderBottomRightRadius:this.radius} : {borderBottomRightRadius:this.radius};
      }
      return this.vertical ? {borderBottomLeftRadius:this.radius,borderBottomRightRadius:this.radius} : {borderTopRightRadius:this.radius,borderBottomRightRadius:this.radius};
    },
    checkedStyle() {
      if (this.mode == 'underline') {
        return {
          color: this.theme.color,
          borderBottomWidth: '2px',
          borderBottomStyle: 'solid',
          borderBottomColor: this.theme.color
        }
      } else if (this.mode == 'excel') {
        return {
          borderColor: this.borderColor,
          borderWidth: '0 1px 4px 0',
          borderBottomColor: this.theme.color,
          backgroundColor: '#f5f5f5',
          height: '86%',
          display: 'flex',
          alignItems: 'center',
          color: '#232323'
        }
      } else {
        return {
          backgroundColor: this.theme.color,
          borderColor: this.theme.color,
          color: '#fff'
        };
      }
    },
    uncheckedStyle() {
      if (this.mode == 'underline') {
        return null;
      } else if (this.mode == 'excel') {
        return {
          borderColor: this.borderColor,
          borderWidth: '0 1px 0 0',
          height: '48.8%',
          display: 'flex',
          alignItems: 'center',
          position: 'relative',
          top: '25.6%',
        }
      } else {
        return {
          backgroundColor: '#fff',
          borderColor: this.borderColor
        };
      }
    },
    gapStyle() {
      if (this.vertical) {
        let padding = this.vertical && this.padding == '0 10px' ? '10px' : this.padding;
        return this.gap <= 0 ? {padding: padding, whiteSpace:'normal'} : {borderTopWidth: '1px', marginBottom: this.gap+'px', padding: padding, whiteSpace:'normal'};
      } else {
        return this.gap <= 0 ? {padding: this.padding} : {borderLeftWidth: '1px', marginRight: this.gap+'px', padding: this.padding};
      }
    },
    widthStyle() {
      return this.widthIsSet ? {width: '100%'} : null;
    },
  }
};
