import CarTreeToolbar from "./CarTreeToolbar.vue";

import Storage from '@/common/cache/Storage.js';

import { getCars } from '@/common/cache/CacheTeamCar.js';
import { getCarById } from '@/common/cache/CacheTeamCar.js';
import { regetNodes } from '@/common/cache/CacheTeamCar.js';
import { refreshAll } from '@/common/cache/CacheTeamCar.js';
import { State } from '@/common/cache/CacheTeamCar.js';
import { getCarTreeCfg } from '@/common/cache/CacheTeamCar.js';
import { onCarTreeAdd, onCarTreeRemove } from '@/common/cache/CacheTeamCar.js';

import { getCarTreeIconUrl } from '@/common/util/Urls.js';
import { getCarStateCnMap } from '@/common/Vars.js';
import { objClone } from '@/common/util/Objects.js';

export default {
  components: { CarTreeToolbar },
  props: {
    setting:     { default: ()=>{return {}} },
    disabled:    { type: Boolean, default: false },
    border:      { type: Boolean, default: true  },
    miniBar:     { type: Boolean, default: false },
    borderColor: { default: $_theme.border_color_dark },
    searchable:  { type: Boolean, default: true  },
    onTreeInited: Function,
    onTreeRefreshed: Function,
    onSearchItemSelected: Function
  },

  _tree: null,
  _carTreeCfg: null,
  _carStateCnMap: null,
  _isStateFun: null,

  getTreeRoots: callback => getCars(cars => callback(cars.tree)),
  getNodeText: node => node.plate ? node[getCarTreeCfg().sortField] : node.name,

  data() {
    return {
      loading: false,
      treeId: 'tree_' + $_theme.zindex(),
      search: {placeholder: null, hasOwnField: null}
    };
  },

  mounted() {
    this.loading = true;
    this._carTreeCfg = getCarTreeCfg();
    this.setStateFun(this._carTreeCfg.filter);
    this._carStateCnMap = getCarStateCnMap();
    this.searchable && this.$refs.toolbar.init();
    getCars(this.onCarLoaded);
  },

  computed: {
    hw: function() { return this.miniBar ? 25 : this.theme.inputHeight; }
  },

  methods: {
    setStateFun(filter) {
      if (filter) {
        filter = filter.charAt(0).toUpperCase() + filter.substr(1);
        this._isStateFun = State['is' + filter];
      } else {
        this._isStateFun = null;
      }
    },
    refreshClick() {
      this.loading = true;
      refreshAll(e => {
        this.loading = false;
        this.searchable && this.$refs.toolbar.showTip('刷新失败', '#f00');
      });
    },
    onRefresh(cars,teams) {
      let snodes = this._tree.getSelectedNodes();
      let cnodes = (this._setting.check && this._setting.check.enable) ? this._tree.getCheckedNodes(true,true) : null;
      this.onCarLoaded(cars, teams);
      this.searchable && this.$refs.toolbar.showTip('刷新成功', '#00f');
      snodes = regetNodes(snodes);
      snodes.length > 0 && this._tree.selectNode(snodes[0]);
      cnodes = regetNodes(cnodes);
      cnodes && cnodes.length > 0 && this.checkNodes(cnodes, true, true);
      this.onTreeRefreshed && this.onTreeRefreshed(cars);
    },
    onCarLoaded(cars,teams) {
      if (!this._setting) {
        this._setting = objClone(this.setting);
        this._setting.view = this._setting.view || {};
        this._setting.view.nameFunction = this.nameFunction;
        this._setting.view.iconUrlFunction = this.iconUrlFunction;
        this._setting.view.iconCssFunction = this.iconCssFunction;
        this._setting.view.titleFunction = this.titleFunction;
        this._isStateFun && (this._setting.view.hideFunction = this.hideFunction);//所有车辆，不设置hideFunction，hideFunction的变更见CarTreeToolbar

        this._setting.callback = this._setting.callback || {};
        if (this._setting.check && this._setting.check.enable) {
          this.__onDblClick = this.__onDblClick || this._setting.callback.onDblClick;
          this._setting.callback.onDblClick = this.onDblClick;
          this.__onCheck = this.__onCheck || this._setting.callback.onCheck;
          this._setting.callback.onCheck = this.onCheck;
        } else {
          this.__onClick = this.__onClick || this._setting.callback.onClick;
          this._setting.callback.onClick = this.onClick;
        }
      }

      cars.root[this.treeId] = {open: true};
      this._tree = $.fn.zTree.init($(this.$refs.treeUl), this._setting, cars.root);
      this.searchable && this.$refs.toolbar.setTree(this._tree);
      this.loading = false;
      onCarTreeAdd(this.treeId, this);
      cars.list.length < 100 && this._tree.expandAll(true);
      this.onTreeInited && this.onTreeInited(cars,teams,this._tree,this);
    },
    showRecent(box) {
      let ids = Storage.get('recent_cars', null);
      if (!ids || ids.length == 0) return;
      let cars = [];
      ids.forEach(id => getCarById(id) && cars.push(getCarById(id)));
      cars.length > 0 && box.openOptions(cars);
    },
    onCheck(node, chgs, value, event) {
      this.__onCheck && this.__onCheck(node, chgs, value, event);
      if (value !== 1) return;
      let carIds = [];
      for (let i=0, len=chgs.length, car; i<len; i++) {
        car = chgs[i];
        if (car.plate) {
          carIds.push(car.id);
          if (carIds.length == 10) return Storage.set('recent_cars', carIds);
        }
      }
      if (carIds.length == 0) return;
      let ids = Storage.get('recent_cars', null);
      if (!ids || ids.length == 0) return Storage.set('recent_cars', carIds);
      for (let i=0, len=ids.length; i<len; i++) {
        if (carIds.indexOf(ids[i]) == -1) {
          carIds.push(ids[i]);
          if (carIds.length == 10) break;
        }
      }
      Storage.set('recent_cars', carIds);
    },
    onClick(node, event) {
      this.__onClick && this.__onClick(node, event);
      if (!node.plate) return;
      let ids = Storage.get('recent_cars', null);
      if (!ids || ids.length == 0) return Storage.set('recent_cars', [node.id]);
      if (ids[0] == node.id) return;
      let i = ids.indexOf(node.id);
      i != -1 && ids.splice(i, 1);
      ids.unshift(node.id);
      ids.length > 10 && ids.pop();
      Storage.set('recent_cars', ids);
    },
    onDblClick(node, event) {
      this.__onDblClick && this.__onDblClick(node, event);
      !node[this.treeId].value && node.plate && this._tree.checkNode(node, true, true, true);
    },
    iconUrlFunction(node, open) {//车辆
      return node.plate ? getCarTreeIconUrl(node.state, node.stts, node.cmpId) : null;
    },
    iconCssFunction(node, open) {//车队
      return node.plate ? null : (open ? 'ico_open' : 'ico_close');
    },
    titleFunction(node) {
      return node.plate ? this._carStateCnMap[node.state] : null;
    },
    hideFunction(node) {
      return !!(node.plate && this._isStateFun && !this._isStateFun(node.state));
    },
    nameFunction(node) {
      if (node.plate) {//车辆
        if (!this._carTreeCfg.showCarField) {// 没有显示其它字段，则直接返回车牌号或车辆名
          return node[this._carTreeCfg.sortField];
        }
        let array = [];
        if (this._carTreeCfg.showCarField.tmnKey) {
          array.push('终端标识'+':'+(node.viceKeys?(node.tmnKey+','+node.viceKeys.replace(/\_va|\_av|\_v|\_a/g, '')):node.tmnKey));
        }
        if (this._carTreeCfg.showCarField.state) {
          array.push(this._carStateCnMap[node.state]);
        }
        return node[this._carTreeCfg.sortField] +　' [' + array.join(' ') + ']';
      } else {//车队
        if (this.model || !this._carTreeCfg.showTeamCount) {// 没有显示其它统计字段，则直接返回l队名
          return node.name;
        }
        let array = [];
        if (this._carTreeCfg.showTeamCount.line) {
          array.push(node.online + '/' + node.carNum);
        }
        if (this._carTreeCfg.showTeamCount.move) {
          array.push('行:' + node.move);
          array.push('停:' + node.stop);
        }
        if (this._carTreeCfg.showTeamCount.alarm) {
          array.push('警:' + node.alarm);
        }
        if (this._carTreeCfg.showTeamCount.invalid) {
          array.push('无效:' + node.invalid);
        }
        return node.name + ' ['+array.join(' ')+']';
      }
    },
    getCheckedNodes(onlyChecked=true, onlyTop=false, field=null, hasOwnField=null, nodes=null) {
      return this._tree ? this._tree.getCheckedNodes(onlyChecked, onlyTop, field, hasOwnField, nodes) : [];
    },
    async checkNodes(nodes, checked=true, checkTypeFlag, callbackFlag) {
      while (this.loading) await sleep(10);
      for (let i=0,len=nodes.length; i<len; i++) {
        this._tree.checkNode(nodes[i], checked, checkTypeFlag, callbackFlag);
      }
    },
    async selectNode(node, isSilent=false) {
      while (this.loading) await sleep(10);
      this._tree.selectNode(node, false, isSilent);
    },
    getTree() { return this._tree; },
    getTreeId() { return this.treeId; },
    destroy() {
      onCarTreeRemove(this.treeId);
      if (this._tree) {
        this._tree.destroy();
        this._tree = null;
      }
      if (this.searchable && this.$refs.toolbar) {
        this.$refs.toolbar.destroy();
      }
      this._carTreeCfg = null;
      this._carStateCnMap = null;
    }
  },

  beforeDestroy() {
    this.destroy();
  }
}

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}
