import { getCars } from '@/common/cache/CacheTeamCar.js';
import { getAlarmType }  from '@/common/cache/CacheAlarm.js';
import BinViewingWindow from './BinViewingWindow.vue';
import Labels from '@/common/gps/Labels.js';

import '@/components/ImgSwiper/swiper.css';


export default {
  data: function() {
    return {
      medias: {},
      mediasList: [],
      url: null,
      o_url: null,
      type: null,
      callback: null,
      waiting: false,
      top: 0,
      rotateTimes: 0,
      imgMaxHeight: 800,
      margin: 0,
      isHide: false,
      index: null,
    };
  },
  _car_map: null,
  _alarm_map: null,
  mySwiper: null,
  created() {
    getCars(cars =>{
      this._car_map = cars.map;
    });
    getAlarmType(alarm =>{
      this._alarm_map = alarm.map;
    });
    this.imgMaxHeight = document.body.clientHeight - 86 - 87;
  },
  computed: {
    Labels: () => Labels,
  },
  mounted() {
    if(this.type == '2'){
      this.getVideoSrc();
    }
  },
  methods: {
    onBinDbclick(){
      $_popup.open(BinViewingWindow, {data: this.url});
    },
    videoError(e){
      this.o_url = this.url;
      this.createFlv();
    },
    getVideoSrc(){
      try {
        this._video = this.$refs.video;
        let that = this;
        this._video.addEventListener("error",that.videoError,false);
      } catch (error) {

      }
    },
    createFlv(){
      $_main.http.post('/query/media?createFlv',{media: this.medias}).then(result => {
        this.url = this.o_url.replace('.mp4','.new.mp4').replace('.h264','.new.mp4');
        let that = this;
        this._video.removeEventListener("error",that.videoError,false);
      });
    },
    swiperDisabled(add){
      if((this.index + add)<0||(this.index + add)==(this.mediasList.length)){
        return true;
      }
      return false;
    },
    nextClick(add){
      this.callback(this.index+add);
      this.$refs.w.close();
    },
    mouseOver(){
      this.isHide = true;
    },
    mouseLeave(){
      this.isHide = false;
    },
    plateFormatter ( cellValue ) {
      cellValue = this._car_map[cellValue];
      return cellValue ? cellValue.plate : '';
    },
    speedFormatter (cellValue){
      if(cellValue){
        return JSON.parse(cellValue).speed!=null ? JSON.parse(cellValue).speed+'km/h' : '';
      }
      return '';
    },
    reasonFormatter ( cellValue, row ) {
      let alarmType = !JSON.parse(cellValue).alarmType ? $_L.get('其他') : JSON.parse(cellValue).alarmType;
      if(!this._alarm_map[alarmType]){
        alarmType = $_L.get('其他');
      }
      if(alarmType == $_L.get('其他')){
        if(row.event == "0"){
          cellValue = $_L.get('平台下发指令');
        }else if(row.event == "1"){
          cellValue = $_L.get('定时动作');
        }else if(row.event == "2"){
          cellValue = $_L.get('抢劫报警触发');
        }else if(row.event == "3"){
          cellValue = $_L.get('碰撞侧翻报警触发');
        }else if(row.event == "4"){
          cellValue = $_L.get('开门拍照');
        }else if(row.event == "5"){
          cellValue = $_L.get('门关拍照');
        }else if(row.event == "6"){
          cellValue = $_L.get('车门由开变关，时速从小于20公里到超过20公里');
        }else if(row.event == "7"){
          cellValue = $_L.get('定距拍照');
        }else if(row.event == "9"){
          cellValue = $_L.get('终端自动');
        }else if(row.event == "100"){
          cellValue = $_L.get('人脸对比');
        }
      }else{
        cellValue = this._alarm_map[alarmType].name;
      }
      return cellValue ? cellValue : '';
    },
    rollImg(){
      /* 获取当前页面的缩放比
          若未设置zoom缩放比，则为默认100%，即1，原图大小
      */
      var zoom = parseInt(this.$refs.img.style.zoom) || 100;
      /* event.wheelDelta 获取滚轮滚动值并将滚动值叠加给缩放比zoom
            wheelDelta统一为±120，其中正数表示为向上滚动，负数表示向下滚动
        */
      zoom += event.wheelDelta / 12;
      /* 最小范围 和 最大范围 的图片缩放尺度 */
      let top = parseInt(this.$el.style.top.replace('px',''));
      if (zoom >= 35 && zoom*this.$refs.img.height/100 < (this.imgMaxHeight-top)) {
        this.$refs.img.style.zoom = zoom + "%";
      }
    return false;

    },
    getTypeName(type){
      if(type == 0){
        return $_L.get('照片');
      }else if(type == 1){
        return $_L.get('音频');
      }else if(type == 2){
        return $_L.get('视频');
      }else if(type == 3){
        return $_L.get('文件');
      }else{
        return $_L.get('其他');
      }
    },
    isImg(type){
      if(type == 0){
        return true;
      }else{
        return false;
      }
    },
    copyUrl(){
      var Url2=this.url;
      var oInput = document.createElement('input');
      oInput.value = Url2;
      document.body.appendChild(oInput);
      oInput.select(); // 选择对象
      document.execCommand("Copy"); // 执行浏览器复制命令
      oInput.className = 'oInput';
      oInput.style.display='none';
    },
    onRotateClick(type){
      this.copyUrl();
      var img = document.getElementById("img");
      var content = document.getElementById("content");
      if(type == "right"){
        this.rotateTimes ++;
      }else if(type == "left"){
        this.rotateTimes --;
      }else{
        this.rotateTimes = 0;
        this.$refs.img.style.zoom = "100%";
      }
      img.style.webkitTransform = 'rotate('+90*this.rotateTimes+'deg)';
      img.style.mozTransform = 'rotate('+90*this.rotateTimes+'deg)';
      img.style.msTransform = 'rotate('+90*this.rotateTimes+'deg)';
      img.style.oTransform = 'rotate('+90*this.rotateTimes+'deg)';
      img.style.transform = 'rotate('+90*this.rotateTimes+'deg)';
      if(this.rotateTimes % 2 != 0){
        this.margin = (img.clientWidth-img.clientHeight)/2;
        this.top = parseInt(this.$el.style.top.replace('px',''));
      }else{
        this.margin = 0;
      }
    },
    onDownload(original = true){
      let url = original ? this.url : this.o_url;
      let suffix = (this.type == '2'&&this.o_url&&original)? 'new.mp4' : url.split("refPath=")[1].split(".")[1];
      let time = this.medias.time.replace(" ","_").replace(new RegExp(":",("gm")),"") + "." +suffix;
      let fileName = this.plateFormatter(this.medias.carId) + "_" + time ;
      var xhr = new XMLHttpRequest();
      xhr.responseType = "blob";  // 返回类型blob
      xhr.open('get', url, true);
      xhr.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');
      // 定义请求完成的处理函数，请求前也可以增加加载框/禁用下载按钮逻辑
      var that = this;
      xhr.onload = function () {
        // 请求完成
        if (this.status === 200) {
          // 返回200
          var blob = this.response;
          var reader = new FileReader();
          reader.readAsDataURL(blob);  // 转换为base64
          reader.onload = function (e) {
            // 转换完成，创建一个a标签用于下载
            var a = document.createElement('a');
            a.download = fileName;
            a.href = e.target.result;
            $("body").append(a);  // 修复firefox中无法触发click
            a.click();
            $(a).remove();
            that.waiting = false;
          }
        }else{
          $_alert.error($_L.get('下载失败！'));
        }
      };
      // 发送ajax请求
      xhr.send();
    },  
  }
};
