import MediaViewingWindow from './MediaViewingWindow.vue';
import AlarmBinContentBox from './AlarmBinContentBox.vue';
import { getCars } from '@/common/cache/CacheTeamCar.js';
import { dateFormat } from '@/common/util/Dates.js';
import Labels from '@/common/gps/Labels.js';
import { getCarById } from '@/common/cache/CacheTeamCar.js';


/*
* 使用例子
* <AlarmMediaBox :medias="medias" :alarm="alarm" />
*/

export default {
  props: {
    medias: { type: Array, default: null },  // 报警多媒体对象
    alarm: { default: null },  // 报警对象     
    weChatGroupList: { type: Array, default: () => [] } //群列表 
  },
  components: { AlarmBinContentBox },
  data() {
    return {
      selectMedia: {},
      url: null,
      o_url: null,
      title: "",
      type: null,
      showTime: false,
      mediasList: null,

    };
  },
  created() {
    getCars(cars => {
      this._car_map = cars.map;
    });

  },
  watch: {
    medias(val, oldVal) {
      if (val != oldVal) {
        this.mediasList = this.sortMedias(this.medias);
        this.onMediasClick(this.mediasList[0]);
      }
    }
  },
  mounted() {
    this.onMediasClick(this.medias[0]);

  },
  methods: {
    isShowAccessory() {
      let append = $_cache.sysCfg['media.adas.append']
      if (append) {
        return JSON.parse(append)['enabled'] == "false" ? false : true;
      }
      return true;
    },
    sortMedias(medias) {
      if (medias && medias.length > 0 && medias[0].type != '0') {
        let newMedias = [];
        for (let i = 0; i < medias.length; i++) {
          if (medias[i].type == '0') {
            newMedias.unshift(medias[i]);
          } else {
            newMedias.push(medias[i]);
          }
        }
        return newMedias;
      }
      return medias;
    },
    guid() {
      return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
        var r = Math.random() * 16 | 0,
          v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
      });
    },
    appendMedias(medias, uid) {
      return $_main.http.syncPost('h5/query/media?append', { uid: uid, medias: medias });
    },
    downloadMedias(uid) {
      var file = "media" + dateFormat(null, "YYYYMMDDHHNNSS");
      let session = $_main.sessionId;
      let userId = $_main.userId;
      let loginWay = $_cache.user.loginWay;
      // let loginWay = 'interface';
      let loginType = $_cache.user.loginType;
      // let loginType = 'user';
      let loginLang = $_cache.user.loginLang;
      // let loginLang = 'zh_CN';
      var url = $_main.domain.WEB_PATH + "media";
      var sendData = "sessionId=" + session + "&userId=" + userId + "&loginWay=" + loginWay + "&loginType=" + loginType + "&loginLang=" + loginLang + "&file=" + file + "&compress=false&uid=" + uid;
      var a = document.createElement('a');
      a.download = file;
      a.href = url + "?" + sendData;
      $("body").append(a);  // 修复firefox中无法触发click
      a.click();
      $(a).remove();
    },
    onDownloadAll() {
      let uid = this.guid();
      let medias = this.medias;
      if (medias.length <= 1000) {
        if (this.appendMedias(medias, uid)) {
          $_alert.success($_L.get('正在下载，请稍后...'));
          this.downloadMedias(uid);
        } else {
          return $_alert.error($_L.get('添加下载失败！'));
        };
      } else {
        var success = true;
        for (var i = 0; i < medias.length; i += 1000) {
          if (!this.appendMedias(medias.slice(i, i + 1000), uid)) {
            success = false;
          };
        }
        if (success) {
          $_alert.success($_L.get('正在下载，请稍后...'));
          this.downloadMedias(uid);
        } else {
          return $_alert.error($_L.get('添加下载失败！'));
        }
      }
    },
    accessoryClick() {
      if(this._car_map[this.alarm.carId].status <= 4){ //判断车辆是否离线
        return $_alert.warn($_L.get('该车辆离线无法补传！'));
      }
      $_main.http.post('h5/mnt/alarm?requestAdasFile', this.alarm).then(result => {
        result && $_alert.success($_L.get('补传成功!'), this.$el) || $_alert.warn($_L.get('补传失败!'), this.$el)
      })
    },
    createFlv() {
      $_http.post('h5/query/media?createFlv', { media: this.selectMedia }).then(result => {
        this.url = this.getMediaUrl(result).replace('.mp4', '.new.mp4').replace('.h264', '.new.mp4');
        this.title = this.url;
        let that = this;
        this._video.removeEventListener("error", that.videoError, false);
      });
    },
    videoError(e) {
      this.o_url = this.url;
      this.createFlv();
    },
    getVideoSrc() {
      try {
        this._video = this.$refs.video;
        let that = this;
        this._video.addEventListener("error", that.videoError, false);
      } catch (error) {

      }
    },
    onMediasClick(media) {
      if (media == null) {
        this.url = null;
        this.title = "";
        this.selectMedia = null;
        return
      }
      this.title = this.getMediaUrl(media);
      this.selectMedia = media;
      this.url = this.getMediaUrl(media);
      this.type = this.selectMedia.type
      if (media.type == "2") {
        this.$nextTick(() => { this.getVideoSrc() });
      }
    },
    onMediasDbclick() {
      let url = this.getMediaUrl(this.selectMedia);
      let index = this.medias.findIndex(item => this.selectMedia == item)
      $_popup.open(MediaViewingWindow, { medias: this.selectMedia, url: url, type: this.type, mediasList: this.mediasList, index: index, callback: this.onMediasDbclickCallback });
    },
    onMediasDbclickCallback(index) {
      let selectMedia = this.medias[index]
      let url = this.getMediaUrl(selectMedia);
      $_popup.open(MediaViewingWindow, { medias: selectMedia, url: url, type: selectMedia.type, mediasList: this.mediasList, index: index, callback: this.onMediasDbclickCallback });
    },
    getImgSrc(media) {
      let type = media.type;
      if (type == 0) {
        return this.getMediaUrl(media);
      } else if (type == 1 || type == 2) {
        return require('@/assets/icon/video.png');
      } else if (type == 3) {
        return require('@/assets/icon/file.png');
      } else if (type == 4) {
        return require('@/assets/icon/file2.png');
      }
    },
    getMediaUrl(media) {
      let format = media.format;
      if (format == 0) {
        format = "jpg";
      } else if (format == 1) {
        format = "tif"
      } else if (format == 2) {
        format = "mp3"
      } else if (format == 3) {
        format = "wav"
      } else if (format == 4) {
        format = "wmv"
      } else if (format == 5) {
        format = "mp4"
      } else if (format == 6) {
        format = "bin"
      } else if (format == 7) {
        format = "xx"
      } else if (format == 8) {
        format = "log"
      } else if (format == 9) {
        format = "yy"
      }
      let dt = media.time.split(' ');
      dt[1] = dt[1].replace(new RegExp(':', 'g'), '');
      let fileName = JSON.parse(media.exts).fileName ? JSON.parse(media.exts).fileName : "";
      fileName = fileName.length > 0 ? (dt[1] + "_" + fileName) : (dt[1] + "." + format);
      return $_main.domain.WEB_PATH + "photos?refPath=" + dt[0] + "/" + media.carId + "/" + fileName;
    },
    plateFormatter(cellValue) {
      cellValue = this._car_map[cellValue];
      return cellValue ? cellValue.plate : '';
    },
    onDownload(original = true) {
      let url = original ? this.url : this.o_url;
      console.log(this.url,this.o_url);
      let suffix = (this.type == '2' && this.o_url && original) ? 'new.mp4' : url.split("refPath=")[1].split(".")[1];
      let time = this.selectMedia.time.replace(" ", "_").replace(new RegExp(":", ("gm")), "") + "." + suffix;
      let fileName = this.plateFormatter(this.selectMedia.carId) + "_" + time;
      var xhr = new XMLHttpRequest();
      xhr.responseType = "blob";  // 返回类型blob
      xhr.open('get', url, true);
      xhr.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');
      // 定义请求完成的处理函数，请求前也可以增加加载框/禁用下载按钮逻辑
      var that = this;
      xhr.onload = function () {
        // 请求完成
        if (this.status === 200) {
          // 返回200
          var blob = this.response;
          var reader = new FileReader();
          reader.readAsDataURL(blob);  // 转换为base64
          reader.onload = function (e) {
            // 转换完成，创建一个a标签用于下载
            var a = document.createElement('a');
            a.download = fileName;
            a.href = e.target.result;
            $("body").append(a);  // 修复firefox中无法触发click
            a.click();
            $(a).remove();
            that.waiting = false;
          }
        } else {
          $_alert.error($_L.get('下载失败！'));
        }
      };
      // 发送ajax请求
      xhr.send();
    },
  },
};
